def changeImageColor(fpathe,name):
    
    im = Image.open(os.path.join(fpathe,name)) #加载指定图片

    pix = im.load()      #获取图片像素
    width = im.size[0]   #获取图片横向像素点数量
    height = im.size[1]  #获取图片纵向像素点数量
    
    #原始像素值的范围
    DPointR = [0,1]
    DPointG = [170,171,172]
    DPointB = [188,189,190]
    #需要计算替换的颜色数组

    listtest = []
    #用于存储遍历的图片rgba值
    r = 0.00
    g = 0.00
    b = 0.00
    a = 255.00
    
    flag = 0

    for x in range(width):
        for y in range(height):
            #遍历像素点
            if im.mode == "RGBA":
                r,g,b,a = pix[x, y]

            if im.mode == "RGB":
                r,g,b = pix[x, y]

#            if not r == 0 and not r == 236:
#            print r,g,b
#                pointRGB2 = [r,g,b]
#                if not pointRGB2 in listtest:
#                    listtest.append(pointRGB2)
            #将RGB转化成数组
            pointRGB = [r,g,b]
                
            if r in DPointR and g in DPointG and b in DPointB:
                if im.mode == "RGBA":
                    im.putpixel((x,y), (int(red),int(green),int(blue),int(a)))
                
                if im.mode == "RGB":
                    im.putpixel((x,y), (int(red),int(green),int(blue)))
            

            elif pointRGB in listAll:
#                print r,DPointR[0]

                #计算透明比例
                rate = (r - DPointR[0])/(255-DPointR[0])
                TR = (255 - red)*rate+red
                TG = (255 - green)*rate+green
                TB = (255 - blue)*rate+blue
                
                if im.mode == "RGBA":
                    im.putpixel((x,y), (int(TR),int(TG),int(TB),int(a)))

                if im.mode == "RGB":
                    im.putpixel((x,y), (int(TR),int(TG),int(TB)))

            elif r == 0 and g == 96 and b == 138: #按钮选中颜色
                if im.mode == "RGBA":
                    im.putpixel((x,y), (int(red-60),int(green-60),int(blue-60),int(a)))

                if im.mode == "RGB":
                    im.putpixel((x,y), (int(red-60),int(green-60),int(blue-60)))
#
#            elif r == 232 and g == 248 and b == 250:
#
#                rate = 0.91
#
#                tr = 255 if (255 - red)*rate+red>=255 else (255 - red)*rate+red;
#                tg = 255 if (255 - green)*rate+green>=255 else (255 - green)*rate+green;
#                tb = 255 if (255 - blue)*rate+blue>=255 else (255 - blue)*rate+blue;
#
#                if im.mode == "RGBA":
#                    im.putpixel((x,y), (int(tr),int(tg),int(tb),int(a)))
#
#                if im.mode == "RGB":
#                    im.putpixel((x,y), (int(tr),int(tg),int(tb)))
#
            elif r == 14 and g == 184 and b == 203:  #iOS deviceinfo界面几个图标

                if im.mode == "RGBA":
                    im.putpixel((x,y), (int(red+30),int(green+30),int(blue+30),int(a)))

                if im.mode == "RGB":
                    im.putpixel((x,y), (int(red+30),int(green+30),int(blue+30)))

            elif pointRGB in listDeep:
                #获取目标
                tr = (red - 60) if (red - 60)>0 else 0;
                tg = (green - 60) if (green - 60)>0 else 0;
                tb = (blue - 60) if (blue - 60)>0 else 0;
                #计算透明比例
                rate = (r - 0)/(255-0)
                TR = (255 - tr)*rate+tr
                TG = (255 - tg)*rate+tg
                TB = (255 - tb)*rate+tb

                if im.mode == "RGBA":
                    im.putpixel((x,y), (int(TR),int(TG),int(TB),int(a)))

                if im.mode == "RGB":
                    im.putpixel((x,y), (int(TR),int(TG),int(TB)))

            else:
                if not a == 0 and not r == 255 and not b == 0 and not g == 0 and not r == g:
#                    print r,g,b,a
                    pointRGB2 = [r,g,b]
                    if not pointRGB2 in listtest:
                        listtest.append(pointRGB2)

#    if len(listtest) > 0:
#        print "list_"+name+" = "
#        print listtest

    im.save(os.path.join(fpathe,name),"PNG")
